import React, { useState } from "react";
import { Route, Switch } from "react-router-dom";
import Home from "./Components/Home";
import SketchPad from "./Components/SketchPad";
import Pictures from "./Components/Pictures";
import NavBar from "./Components/NavBar";
import Loading from "./Components/loading";
import { useAuth0 } from "@auth0/auth0-react";
import ProtectedRoute from "./auth/protected-route";

function App() {
  const [pictures, setPictures] = useState([]);
  const [feed, setFeed] = useState([]);
  const [user, setUser] = useState("hank95");

  useState(() => {
    fetch("/api")
      .then((res) => res.json())
      .then((json) => {
        setFeed(json);
      });
  }, []);

  const { isLoading } = useAuth0();
  if (isLoading) {
    return <Loading />;
  }
  const handleSave = (e, title) => {
    if (
      e.target.previousSibling.toDataURL() ===
      "data:image/png;base64,"
    ) {
      return;
    }
    const pic = e.target.parentElement.previousSibling.toDataURL();
    fetch("/api", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        title: title,
        author: user,
        image: pic,
      }),
    })
      .then((res) => res.json())
      .then((json) => {
        setFeed([...feed, json]);
      });

    setPictures([...pictures, pic]);
  };

  const handleDelete = (id) => {
    const newFeed = feed.filter((item) => item.id !== id);
    setFeed(newFeed);
    fetch(`/api/${id}`, {
      method: "DELETE",
    });
  };

  return (
    <div className="App">
      <NavBar />
      <Switch>
        <Route
          path="/sketch-pad"
          component={() => <SketchPad handleSave={handleSave} user={user} />}
        />
        <ProtectedRoute
          path="/my-sketchs"
          component={() => (
            <Pictures
              userInside={user}
              feed={feed}
              handleDelete={handleDelete}
            />
          )}
        />
        <Route
          exact
          path="/"
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            height: "100vh",
          }}
          component={() => <Home feed={feed} />}
        />
      </Switch>
    </div>
  );
}

export default App;
