import React, { useState } from "react";
import { Route, Switch } from "react-router-dom";
import Axios from "axios";
import Home from "./Components/Home";
import SketchPad from "./Components/SketchPad";
import Pictures from "./Components/Pictures";
import NavBar from "./Components/NavBar";
import Loading from "./Components/loading";
// import { useAuth0 } from "@auth0/auth0-react";
// import ProtectedRoute from "./auth/protected-route";

function App() {
  const [feed, setFeed] = useState([]);
  const [user, setUser] = useState("hank95");

  useState(() => {
    Axios.get("http://localhost:3001/api/get").then((response) => {
      setFeed(response.data);
    });
  }, []);

  // const { isLoading } = useAuth0();
  // if (isLoading) {
  //   return <Loading />;
  // }
  const handleSave = (e, title) => {
    if (
      e.target.parentElement.previousSibling.toDataURL() ===
      "data:image/png;base64,"
    ) {
      return;
    }
    const curDate = new Date().toLocaleDateString();
    const pic = e.target.parentElement.previousSibling.toDataURL();
    Axios.post("http://localhost:3001/api/insert", {
      title: title,
      author: user,
      image: pic,
      datePosted: curDate,
    }).then(() => {
      setFeed(...feed, {
        title: title,
        author: user,
        image: pic,
        datePosted: curDate,
      });
    });
  };

  const handleDelete = (id) => {
    console.log(id);
    Axios.delete(`http://localhost:3001/delete/${id}`).then((response) => {
      setFeed(
        feed.filter((val) => {
          return val.iduser_posts !== id;
        })
      );
    });
  };

  return (
    <div className="App">
      <NavBar />
      <Switch>
        <Route
          path="/sketch-pad"
          component={() => <SketchPad handleSave={handleSave} user={user} />}
        />
        <Route
          path="/my-sketchs"
          component={() => (
            <Pictures
              userInside={user}
              feed={feed}
              handleDelete={handleDelete}
            />
          )}
        />
        <Route
          exact
          path="/"
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            height: "100vh",
          }}
          component={() => <Home feed={feed} />}
        />
      </Switch>
    </div>
  );
}

export default App;
